(180,	97,	'ru',	'',	'O:15:\"Nette\\ArrayHash\":4:{s:4:\"text\";s:0:\"\";s:7:\"x_coord\";s:0:\"\";s:7:\"y_coord\";s:0:\"\";s:5:\"image\";s:0:\"\";}'),
(181,	98,	'en',	'Composite mandrels',	'O:15:\"Nette\\ArrayHash\":4:{s:4:\"text\";s:250:\"<p><span>The printing cylinder mandrels are made from composite material and have excellent dynamic characteristics enabling quick and higher quality print and the ability to reach 6% shorter print repeat lengths compared to standard jobs.</span></p>\";s:7:\"x_coord\";s:0:\"\";s:7:\"y_coord\";s:0:\"\";s:5:\"image\";s:0:\"\";}'),
