(192,	100,	'ru',	'',	'O:15:\"Nette\\ArrayHash\":4:{s:4:\"text\";s:0:\"\";s:7:\"x_coord\";s:0:\"\";s:7:\"y_coord\";s:0:\"\";s:5:\"image\";s:0:\"\";}'),
(193,	101,	'en',	'Machine design and ergonomics',	'O:15:\"Nette\\ArrayHash\":4:{s:4:\"text\";s:227:\"<p><span>Modern compact design with aesthetic and ergonomic features maximise operator comfort and minimise accident risks. All eletrcial cabinets are integrated into the machine layout minimising space requirements.</span></p>\";s:7:\"x_coord\";s:0:\"\";s:7:\"y_coord\";s:0:\"\";s:5:\"image\";s:0:\"\";}'),
