(199,	102,	'cn',	'',	'O:15:\"Nette\\ArrayHash\":4:{s:4:\"text\";s:0:\"\";s:7:\"x_coord\";s:0:\"\";s:7:\"y_coord\";s:0:\"\";s:5:\"image\";s:0:\"\";}'),
(200,	102,	'ru',	'',	'O:15:\"Nette\\ArrayHash\":4:{s:4:\"text\";s:0:\"\";s:7:\"x_coord\";s:0:\"\";s:7:\"y_coord\";s:0:\"\";s:5:\"image\";s:0:\"\";}'),
(201,	103,	'en',	'CE standards',	'O:15:\"Nette\\ArrayHash\":4:{s:4:\"text\";s:272:\"<p><span>Machines comply with and meet stringent European safety standards and codes. Operation and operator risk analysis are regularly assessed. Safety design elements are updated and new elements are added according to actual standards and code modifications</span></p>\";s:7:\"x_coord\";s:0:\"\";s:7:\"y_coord\";s:0:\"\";s:5:\"image\";s:0:\"\";}'),
(202,	103,	'cs',	'',	'O:15:\"Nette\\ArrayHash\":4:{s:4:\"text\";s:0:\"\";s:7:\"x_coord\";s:0:\"\";s:7:\"y_coord\";s:0:\"\";s:5:\"image\";s:0:\"\";}'),
